    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        IMC c = new IMC();
        
        try{
            
            c.setAltura(Double.valueOf(calAltura.getText()));
            c.setPeso(Double.valueOf(Peso.getText()));
            c.setGenero(String.valueOf(CBBGenero.getSelectedItem()));
            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Informe os campos corretamente");
        }
        
        double imc;
        
        imc = c.getPeso() / (c.getAltura()*c.getAltura());
        
        if (c.getGenero().equals("Feminino")){
            this.imcFeminino(imc);
        } else {
            this.imcMasculino(imc);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    public void imcFeminino(double imc){
        if(imc < 19.1){
            JOptionPane.showMessageDialog(null, "Abaixo do Peso");
        } else if(imc <= 19.1 && imc < 25.8){
            JOptionPane.showMessageDialog(null, "Peso Normal");
        } else if(imc <= 25.8 && imc < 27.3){
            JOptionPane.showMessageDialog(null, "Marginalmente acima do peso");
        } else if(imc <= 27.3 && imc < 32.3){
            JOptionPane.showMessageDialog(null, "Acima do peso");
        } else {
            JOptionPane.showMessageDialog(null, "Obeso");
        }
    } 
    
    public void imcMasculino(double imc){
         if(imc < 19.1){
            JOptionPane.showMessageDialog(null, "Abaixo do Peso");
        } else if(imc <= 19.1 && imc < 25.8){
            JOptionPane.showMessageDialog(null, "Peso Normal");
        } else if(imc <= 25.8 && imc < 27.3){
            JOptionPane.showMessageDialog(null, "Marginalmente acima do peso");
        } else if(imc <= 27.3 && imc < 32.3){
            JOptionPane.showMessageDialog(null, "Acima do peso");
        } else {
            JOptionPane.showMessageDialog(null, "Obeso");
        }   
    }
    
